import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { TrendingUp, TrendingDown, Wallet, PieChart } from 'lucide-react';
import api from '../utils/api';
import useAutoRefresh from '../hooks/useAutoRefresh';

const Dashboard = () => {
  const [portfolioData, setPortfolioData] = useState(null);
  const [loading, setLoading] = useState(true);
  const token = localStorage.getItem('token');

  const fetchPortfolioData = async () => {
    try {
      console.log('Fetching portfolio data...');
      const response = await api.getPortfolio(token);
      console.log('Portfolio response status:', response.status);

      if (response.ok) {
        const data = await response.json();
        console.log('Portfolio data:', data);
        setPortfolioData(data);
      } else {
        const errorData = await response.json();
        console.error('Portfolio API error:', errorData);
        setPortfolioData(null);
      }
    } catch (error) {
      console.error('Network error fetching portfolio:', error);
      setPortfolioData(null);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPortfolioData();
  }, []);

  // Auto-refresh every 30 seconds
  useAutoRefresh(fetchPortfolioData, 30000);

  if (loading) {
    return <div className="flex justify-center items-center h-64">Loading...</div>;
  }

  if (!portfolioData && !loading) {
    return <div className="text-center text-red-600">Error loading portfolio data</div>;
  }

  const { 
    balance = 0, 
    totalInvested = 0, 
    currentValue = 0, 
    totalGain = 0, 
    gainPercentage = 0, 
    holdings = [] 
  } = portfolioData || {};

  const StatCard = ({ title, value, change, changeType, icon: Icon }) => (
    <div className="card">
      <div className="flex items-center justify-between">
        <div>
          <p className="text-sm font-medium text-gray-600">{title}</p>
          <p className="text-2xl font-bold text-gray-900">₹{value.toLocaleString()}</p>
          {change !== undefined && (
            <div className={`flex items-center mt-1 ${changeType === 'positive' ? 'text-green-600' : 'text-red-600'}`}>
              {changeType === 'positive' ? <TrendingUp className="h-4 w-4 mr-1" /> : <TrendingDown className="h-4 w-4 mr-1" />}
              <span className="text-sm font-medium">₹{Math.abs(change).toLocaleString()} ({Math.abs(gainPercentage)}%)</span>
            </div>
          )}
        </div>
        <div className="p-3 bg-blue-50 rounded-full">
          <Icon className="h-6 w-6 text-blue-600" />
        </div>
      </div>
    </div>
  );

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Dashboard</h1>
        <p className="text-gray-600">Welcome back! Here's your portfolio overview.</p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Wallet Balance"
          value={balance}
          icon={Wallet}
        />
        <StatCard
          title="Total Invested"
          value={totalInvested}
          icon={PieChart}
        />
        <StatCard
          title="Current Value"
          value={currentValue}
          change={totalGain}
          changeType={totalGain > 0 ? 'positive' : 'negative'}
          icon={TrendingUp}
        />
        <StatCard
          title="Total Returns"
          value={totalGain}
          change={totalGain}
          changeType={totalGain > 0 ? 'positive' : 'negative'}
          icon={totalGain > 0 ? TrendingUp : TrendingDown}
        />
      </div>

      {/* Portfolio Growth Chart */}
      <div className="card">
        <div className="mb-6">
          <h2 className="text-lg font-semibold text-gray-900">Portfolio Growth</h2>
          <p className="text-gray-600">Track your investment performance over time</p>
        </div>
        <div className="h-80">
          <ResponsiveContainer width="100%" height="100%">
            <LineChart data={[
              { date: '2024-01', value: totalInvested * 0.9 },
              { date: '2024-02', value: totalInvested * 0.95 },
              { date: '2024-03', value: totalInvested * 0.88 },
              { date: '2024-04', value: totalInvested * 1.05 },
              { date: '2024-05', value: currentValue }
            ]}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" />
              <YAxis />
              <Tooltip formatter={(value) => [`₹${value.toLocaleString()}`, 'Portfolio Value']} />
              <Line 
                type="monotone" 
                dataKey="value" 
                stroke="#2563eb" 
                strokeWidth={2}
                dot={{ fill: '#2563eb', strokeWidth: 2, r: 4 }}
              />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Recent Holdings */}
      <div className="card">
        <div className="flex items-center justify-between mb-6">
          <div>
            <h2 className="text-lg font-semibold text-gray-900">Top Holdings</h2>
            <p className="text-gray-600">Your best performing investments</p>
          </div>
        </div>
        {holdings && holdings.length > 0 ? (
          <div className="space-y-4">
            {(holdings || []).slice(0, 3).map((holding) => (
              <div key={holding.id} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                <div className="flex items-center space-x-4">
                  <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                    <span className="text-blue-600 font-semibold text-sm">
                      {holding.asset_symbol.substring(0, 2)}
                    </span>
                  </div>
                  <div>
                    <p className="font-medium text-gray-900">{holding.asset_symbol}</p>
                    <p className="text-sm text-gray-600">{holding.quantity} shares</p>
                  </div>
                </div>
                <div className="text-right">
                  <p className="font-medium text-gray-900">₹{holding.currentPrice}</p>
                  <p className={`text-sm ${holding.pnl > 0 ? 'text-green-600' : 'text-red-600'}`}>
                    {holding.pnl > 0 ? '+' : ''}₹{holding.pnl.toFixed(2)} ({holding.pnlPercentage}%)
                  </p>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-8 text-gray-500">
            No holdings yet. Start investing to see your portfolio here!
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
