import React, { useState, useEffect } from 'react';
import { Plus, TrendingUp, TrendingDown, Heart, Search } from 'lucide-react';
import api from '../utils/api';

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [newStock, setNewStock] = useState({ symbol: '', name: '' });
  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchWatchlist();
  }, []);

  const fetchWatchlist = async () => {
    try {
      const response = await api.getWatchlist(token);
      const data = await response.json();
      setWatchlist(data);
    } catch (error) {
      console.error('Error fetching watchlist:', error);
    } finally {
      setLoading(false);
    }
  };

  const addToWatchlist = async () => {
    try {
      const response = await api.addToWatchlist(token, {
        symbol: newStock.symbol.toUpperCase(),
        assetName: newStock.name || newStock.symbol
      });

      if (response.ok) {
        fetchWatchlist();
        setShowAddModal(false);
        setNewStock({ symbol: '', name: '' });
      }
    } catch (error) {
      console.error('Error adding to watchlist:', error);
    }
  };

  const filteredWatchlist = watchlist.filter(stock =>
    stock.asset_symbol.toLowerCase().includes(searchTerm.toLowerCase()) ||
    stock.asset_name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const WatchlistCard = ({ stock }) => (
    <div className="card hover:shadow-md transition-shadow cursor-pointer">
      <div className="flex items-center justify-between">
        <div className="flex-1">
          <div className="flex items-center justify-between mb-2">
            <h3 className="font-semibold text-gray-900">{stock.asset_symbol}</h3>
            <Heart className="h-5 w-5 text-red-500 fill-current" />
          </div>
          <p className="text-sm text-gray-600 mb-3">{stock.asset_name}</p>
          <div className="flex items-center justify-between">
            <div>
              <p className="text-xl font-bold text-gray-900">₹{stock.currentPrice}</p>
            </div>
            <div className={`flex items-center ${stock.change > 0 ? 'text-green-600' : 'text-red-600'}`}>
              {stock.change > 0 ? <TrendingUp className="h-4 w-4 mr-1" /> : <TrendingDown className="h-4 w-4 mr-1" />}
              <div className="text-right">
                <p className="text-sm font-medium">
                  {stock.change > 0 ? '+' : ''}₹{Math.abs(stock.change)}
                </p>
                <p className="text-xs">
                  ({stock.changePercentage > 0 ? '+' : ''}{stock.changePercentage}%)
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="mt-4 flex space-x-2">
        <button className="flex-1 btn-success text-sm py-1">Buy</button>
        <button className="flex-1 btn-danger text-sm py-1">Sell</button>
      </div>
    </div>
  );

  if (loading) {
    return <div className="flex justify-center items-center h-64">Loading...</div>;
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Watchlist</h1>
          <p className="text-gray-600">Keep track of your favorite stocks and investments</p>
        </div>
        <button 
          onClick={() => setShowAddModal(true)}
          className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
        >
          <Plus className="h-4 w-4 mr-2" />
          Add Stock
        </button>
      </div>

      {/* Search Bar */}
      <div className="card">
        <div className="relative">
          <div className="absolute inset-y-0 left-0 flex items-center pl-3">
            <Search className="h-5 w-5 text-gray-400" />
          </div>
          <input
            type="text"
            placeholder="Search stocks in your watchlist..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="block w-full rounded-md border border-gray-300 bg-white py-2 pl-10 pr-3 text-sm placeholder-gray-500 focus:border-blue-500 focus:outline-none focus:ring-1 focus:ring-blue-500"
          />
        </div>
      </div>

      {/* Watchlist Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredWatchlist.map((stock) => (
          <WatchlistCard key={stock.id} stock={stock} />
        ))}
      </div>

      {filteredWatchlist.length === 0 && (
        <div className="text-center py-12">
          <Heart className="h-12 w-12 text-gray-300 mx-auto mb-4" />
          <p className="text-gray-500 text-lg">No stocks in your watchlist</p>
          <p className="text-gray-400">Add some stocks to get started!</p>
        </div>
      )}

      {/* Add Stock Modal */}
      {showAddModal && (
        <div className="fixed inset-0 z-50 overflow-y-auto">
          <div className="flex items-center justify-center min-h-screen px-4">
            <div className="fixed inset-0 bg-gray-600 bg-opacity-75" onClick={() => setShowAddModal(false)} />
            <div className="relative bg-white rounded-lg max-w-md w-full p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Add to Watchlist</h3>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Stock Symbol
                  </label>
                  <input
                    type="text"
                    placeholder="e.g., RELIANCE, TCS, INFY"
                    value={newStock.symbol}
                    onChange={(e) => setNewStock({...newStock, symbol: e.target.value})}
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Company Name (Optional)
                  </label>
                  <input
                    type="text"
                    placeholder="e.g., Reliance Industries"
                    value={newStock.name}
                    onChange={(e) => setNewStock({...newStock, name: e.target.value})}
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>
                <div className="flex space-x-3">
                  <button 
                    onClick={() => setShowAddModal(false)}
                    className="flex-1 px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50"
                  >
                    Cancel
                  </button>
                  <button 
                    onClick={addToWatchlist}
                    className="flex-1 btn-primary"
                  >
                    Add Stock
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Watchlist;
