import React, { useState, useEffect } from 'react';
import { Trophy, Medal, Award, TrendingUp } from 'lucide-react';
import api from '../utils/api';

const Leaderboard = () => {
  const [leaderboard, setLeaderboard] = useState([]);
  const [loading, setLoading] = useState(true);
  const token = localStorage.getItem('token');
  const currentUser = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    fetchLeaderboard();
  }, []);

  const fetchLeaderboard = async () => {
    try {
      const response = await api.getLeaderboard(token);
      const data = await response.json();
      setLeaderboard(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error fetching leaderboard:', error);
      setLeaderboard([]);
    } finally {
      setLoading(false);
    }
  };

  const getBadgeIcon = (badge) => {
    switch (badge) {
      case 'Gold':
        return <Trophy className="h-5 w-5 text-yellow-500" />;
      case 'Silver':
        return <Medal className="h-5 w-5 text-gray-400" />;
      case 'Bronze':
        return <Award className="h-5 w-5 text-orange-600" />;
      default:
        return null;
    }
  };

  const getRankStyle = (rank) => {
    switch (rank) {
      case 1:
        return 'bg-gradient-to-r from-yellow-400 to-yellow-600 text-white';
      case 2:
        return 'bg-gradient-to-r from-gray-300 to-gray-500 text-white';
      case 3:
        return 'bg-gradient-to-r from-orange-400 to-orange-600 text-white';
      default:
        return 'bg-gray-100 text-gray-700';
    }
  };

  if (loading) {
    return <div className="flex justify-center items-center h-64">Loading...</div>;
  }

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Leaderboard</h1>
        <p className="text-gray-600">See how you rank against other investors</p>
      </div>

      {/* Top 3 Podium */}
      <div className="card">
        <h2 className="text-lg font-semibold text-gray-900 mb-6">Top Performers</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {leaderboard.slice(0, 3).map((user, index) => (
            <div key={user.userId} className={`text-center p-6 rounded-lg ${
              index === 0 ? 'bg-gradient-to-br from-yellow-50 to-yellow-100 border-2 border-yellow-200' :
              index === 1 ? 'bg-gradient-to-br from-gray-50 to-gray-100 border-2 border-gray-200' :
              'bg-gradient-to-br from-orange-50 to-orange-100 border-2 border-orange-200'
            }`}>
              <div className="flex justify-center mb-4">
                {getBadgeIcon(user.badge)}
              </div>
              <div className={`inline-flex items-center justify-center w-12 h-12 rounded-full text-xl font-bold mb-3 ${getRankStyle(user.rank)}`}>
                {user.rank}
              </div>
              <h3 className="font-semibold text-gray-900 mb-2">{user.username}</h3>
              <p className="text-2xl font-bold text-gray-900 mb-1">₹{user.portfolioValue.toLocaleString()}</p>
              <p className={`font-medium ${user.gainPercentage >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                {user.gainPercentage >= 0 ? '+' : ''}{user.gainPercentage}%
              </p>
            </div>
          ))}
        </div>
      </div>

      {/* Full Leaderboard Table */}
      <div className="card">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-lg font-semibold text-gray-900">All Rankings</h2>
          <div className="flex items-center space-x-2 text-sm text-gray-600">
            <TrendingUp className="h-4 w-4" />
            <span>Updated every 15 minutes</span>
          </div>
        </div>

        <div className="overflow-x-auto">
          <table className="min-w-full">
            <thead>
              <tr className="border-b border-gray-200">
                <th className="text-left py-3 px-4 font-medium text-gray-900">Rank</th>
                <th className="text-left py-3 px-4 font-medium text-gray-900">Investor</th>
                <th className="text-right py-3 px-4 font-medium text-gray-900">Portfolio Value</th>
                <th className="text-right py-3 px-4 font-medium text-gray-900">Total Gain</th>
                <th className="text-center py-3 px-4 font-medium text-gray-900">Badge</th>
              </tr>
            </thead>
            <tbody>
              {leaderboard.map((user) => {
                const isCurrentUser = user.userId === currentUser?.id;
                return (
                  <tr 
                    key={user.userId} 
                    className={`border-b border-gray-100 hover:bg-gray-50 ${
                      isCurrentUser ? 'bg-blue-50' : ''
                    }`}
                  >
                    <td className="py-4 px-4">
                      <div className={`inline-flex items-center justify-center w-8 h-8 rounded-full text-sm font-bold ${getRankStyle(user.rank)}`}>
                        {user.rank}
                      </div>
                    </td>
                    <td className="py-4 px-4">
                      <div className="flex items-center">
                        <div className="w-8 h-8 bg-gray-200 rounded-full flex items-center justify-center mr-3">
                          <span className="text-xs font-medium text-gray-600">
                            {user.username.substring(0, 2).toUpperCase()}
                          </span>
                        </div>
                        <div>
                          <p className={`font-medium ${isCurrentUser ? 'text-blue-600' : 'text-gray-900'}`}>
                            {user.username}
                            {isCurrentUser && (
                              <span className="ml-2 text-xs bg-blue-100 text-blue-600 px-2 py-1 rounded-full">
                                You
                              </span>
                            )}
                          </p>
                        </div>
                      </div>
                    </td>
                    <td className="text-right py-4 px-4 font-medium text-gray-900">
                      ₹{user.portfolioValue.toLocaleString()}
                    </td>
                    <td className="text-right py-4 px-4">
                      <span className={`font-medium ${user.gainPercentage >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                        {user.gainPercentage >= 0 ? '+' : ''}{user.gainPercentage}%
                      </span>
                    </td>
                    <td className="text-center py-4 px-4">
                      {user.badge ? (
                        <div className="flex items-center justify-center">
                          {getBadgeIcon(user.badge)}
                          <span className="ml-1 text-sm font-medium text-gray-600">{user.badge}</span>
                        </div>
                      ) : (
                        <span className="text-gray-400 text-sm">-</span>
                      )}
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>

      {/* Achievement Badges */}
      <div className="card">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Achievement Badges</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="flex items-center p-4 bg-yellow-50 rounded-lg">
            <Trophy className="h-8 w-8 text-yellow-500 mr-3" />
            <div>
              <p className="font-medium text-yellow-900">Gold Investor</p>
              <p className="text-sm text-yellow-700">Top 1% performers</p>
            </div>
          </div>
          <div className="flex items-center p-4 bg-gray-50 rounded-lg">
            <Medal className="h-8 w-8 text-gray-500 mr-3" />
            <div>
              <p className="font-medium text-gray-900">Silver Investor</p>
              <p className="text-sm text-gray-700">Top 5% performers</p>
            </div>
          </div>
          <div className="flex items-center p-4 bg-orange-50 rounded-lg">
            <Award className="h-8 w-8 text-orange-500 mr-3" />
            <div>
              <p className="font-medium text-orange-900">Bronze Investor</p>
              <p className="text-sm text-orange-700">Top 10% performers</p>
            </div>
          </div>
        </div>
      </div>

      {/* Competition Info */}
      <div className="card bg-gradient-to-r from-blue-50 to-blue-50">
        <div className="flex items-center justify-between">
          <div>
            <h3 className="text-lg font-semibold text-gray-900 mb-2">Monthly Competition</h3>
            <p className="text-gray-600 mb-4">
              Compete with other investors and win exciting prizes! 
              The top 3 performers each month get special rewards.
            </p>
            <div className="flex items-center space-x-4 text-sm">
              <div className="flex items-center">
                <Trophy className="h-4 w-4 text-yellow-500 mr-1" />
                <span>1st Place: ₹10,000 bonus</span>
              </div>
              <div className="flex items-center">
                <Medal className="h-4 w-4 text-gray-500 mr-1" />
                <span>2nd Place: ₹5,000 bonus</span>
              </div>
              <div className="flex items-center">
                <Award className="h-4 w-4 text-orange-500 mr-1" />
                <span>3rd Place: ₹2,500 bonus</span>
              </div>
            </div>
          </div>
          <div className="text-center">
            <p className="text-sm text-gray-600">Time Remaining</p>
            <p className="text-2xl font-bold text-blue-600">12 days</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Leaderboard;
