import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { TrendingUp, TrendingDown, Wallet, PieChart } from 'lucide-react';
import api from '../utils/api';
import useAutoRefresh from '../hooks/useAutoRefresh';

const Dashboard = () => {
  const [portfolioData, setPortfolioData] = useState(null);
  const [loading, setLoading] = useState(true);
  const token = localStorage.getItem('token');

  const fetchPortfolioData = async () => {
    try {
      console.log('Fetching portfolio data...');
      const response = await api.getPortfolio(token);
      console.log('Portfolio response status:', response.status);

      if (response.ok) {
        const data = await response.json();
        console.log('Portfolio data:', data);
        setPortfolioData(data);
      } else {
        const errorData = await response.json();
        console.error('Portfolio API error:', errorData);
        setPortfolioData(null);
      }
    } catch (error) {
      console.error('Network error fetching portfolio:', error);
      setPortfolioData(null);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPortfolioData();
  }, []);

  // Auto-refresh every 30 seconds
  useAutoRefresh(fetchPortfolioData, 30000);

  if (loading) {
    return <div className="flex justify-center items-center h-64">Loading...</div>;
  }

  if (!portfolioData && !loading) {
    return <div className="text-center text-red-600">Error loading portfolio data</div>;
  }

  const { 
    balance = 0, 
    totalInvested = 0, 
    currentValue = 0, 
    totalGain = 0, 
    gainPercentage = 0, 
    holdings = [] 
  } = portfolioData || {};

  const StatCard = ({ title, value, change, changeType, icon: Icon, gradient }) => (
    <div className={`card hover:shadow-lg transition-all duration-300 ${gradient || 'bg-white'} border-0 shadow-md`}>
      <div className="flex items-center justify-between">
        <div className="flex-1">
          <p className="text-sm font-medium text-gray-600 mb-1">{title}</p>
          <p className="text-2xl font-bold text-gray-900 mb-2">₹{value.toLocaleString()}</p>
          {change !== undefined && (
            <div className={`flex items-center ${changeType === 'positive' ? 'text-green-600' : 'text-red-600'}`}>
              {changeType === 'positive' ? <TrendingUp className="h-4 w-4 mr-1" /> : <TrendingDown className="h-4 w-4 mr-1" />}
              <span className="text-sm font-medium">
                {change > 0 ? '+' : ''}₹{Math.abs(change).toLocaleString()} ({Math.abs(gainPercentage)}%)
              </span>
            </div>
          )}
        </div>
        <div className="p-4 bg-gradient-to-br from-blue-500 to-purple-600 rounded-xl shadow-lg">
          <Icon className="h-7 w-7 text-white" />
        </div>
      </div>
    </div>
  );

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Dashboard</h1>
        <p className="text-gray-600">Welcome back! Here's your portfolio overview.</p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Wallet Balance"
          value={balance}
          icon={Wallet}
          gradient="bg-gradient-to-br from-green-50 to-emerald-50"
        />
        <StatCard
          title="Total Invested"
          value={totalInvested}
          icon={PieChart}
          gradient="bg-gradient-to-br from-blue-50 to-cyan-50"
        />
        <StatCard
          title="Current Value"
          value={currentValue}
          change={totalGain}
          changeType={totalGain > 0 ? 'positive' : 'negative'}
          icon={TrendingUp}
          gradient="bg-gradient-to-br from-purple-50 to-pink-50"
        />
        <StatCard
          title="Total Returns"
          value={totalGain}
          change={totalGain}
          changeType={totalGain > 0 ? 'positive' : 'negative'}
          icon={totalGain > 0 ? TrendingUp : TrendingDown}
          gradient={totalGain > 0 ? 'bg-gradient-to-br from-green-50 to-emerald-50' : 'bg-gradient-to-br from-red-50 to-pink-50'}
        />
      </div>

      {/* Portfolio Growth Chart */}
      <div className="card bg-gradient-to-br from-blue-50 via-white to-purple-50">
        <div className="flex items-center justify-between mb-6">
          <div>
            <h2 className="text-xl font-bold text-gray-900">Portfolio Growth</h2>
            <p className="text-gray-600">Track your investment performance over time</p>
          </div>
          <div className="text-right">
            <p className="text-sm text-gray-500">Current Value</p>
            <p className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
              ₹{currentValue.toLocaleString()}
            </p>
          </div>
        </div>
        <div className="h-80 relative">
          <div className="absolute inset-0 bg-gradient-to-r from-blue-100/20 to-purple-100/20 rounded-lg"></div>
          <ResponsiveContainer width="100%" height="100%">
            <LineChart data={[
              { date: 'Jan', value: totalInvested * 0.9, month: 'January' },
              { date: 'Feb', value: totalInvested * 0.95, month: 'February' },
              { date: 'Mar', value: totalInvested * 0.88, month: 'March' },
              { date: 'Apr', value: totalInvested * 1.05, month: 'April' },
              { date: 'May', value: currentValue, month: 'May' }
            ]}>
              <defs>
                <linearGradient id="colorGradient" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#3b82f6" stopOpacity={0.3}/>
                  <stop offset="95%" stopColor="#3b82f6" stopOpacity={0}/>
                </linearGradient>
              </defs>
              <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" strokeOpacity={0.5} />
              <XAxis 
                dataKey="date" 
                axisLine={false}
                tickLine={false}
                tick={{ fill: '#6b7280', fontSize: 12 }}
              />
              <YAxis 
                axisLine={false}
                tickLine={false}
                tick={{ fill: '#6b7280', fontSize: 12 }}
                tickFormatter={(value) => `₹${(value/1000).toFixed(0)}K`}
              />
              <Tooltip 
                contentStyle={{
                  backgroundColor: 'white',
                  border: 'none',
                  borderRadius: '12px',
                  boxShadow: '0 10px 25px rgba(0,0,0,0.1)'
                }}
                formatter={(value, name, props) => [
                  `₹${value.toLocaleString()}`, 
                  'Portfolio Value'
                ]}
                labelFormatter={(label, payload) => {
                  const data = payload?.[0]?.payload;
                  return data?.month || label;
                }}
              />
              <Line 
                type="monotone" 
                dataKey="value" 
                stroke="url(#gradient)"
                strokeWidth={3}
                dot={{ fill: '#3b82f6', strokeWidth: 2, r: 5, stroke: 'white' }}
                activeDot={{ r: 7, fill: '#3b82f6', stroke: 'white', strokeWidth: 3 }}
                fill="url(#colorGradient)"
              />
              <defs>
                <linearGradient id="gradient" x1="0" y1="0" x2="1" y2="0">
                  <stop offset="0%" stopColor="#3b82f6" />
                  <stop offset="100%" stopColor="#8b5cf6" />
                </linearGradient>
              </defs>
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Recent Holdings */}
      <div className="card">
        <div className="flex items-center justify-between mb-6">
          <div>
            <h2 className="text-lg font-semibold text-gray-900">Top Holdings</h2>
            <p className="text-gray-600">Your best performing investments</p>
          </div>
        </div>
        {holdings && holdings.length > 0 ? (
          <div className="space-y-4">
            {(holdings || []).slice(0, 3).map((holding) => (
              <div key={holding.id} className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                <div className="flex items-center space-x-4">
                  <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                    <span className="text-blue-600 font-semibold text-sm">
                      {holding.asset_symbol.substring(0, 2)}
                    </span>
                  </div>
                  <div>
                    <p className="font-medium text-gray-900">{holding.asset_symbol}</p>
                    <p className="text-sm text-gray-600">{holding.quantity} shares</p>
                  </div>
                </div>
                <div className="text-right">
                  <p className="font-medium text-gray-900">₹{holding.currentPrice}</p>
                  <p className={`text-sm ${holding.pnl > 0 ? 'text-green-600' : 'text-red-600'}`}>
                    {holding.pnl > 0 ? '+' : ''}₹{holding.pnl.toFixed(2)} ({holding.pnlPercentage}%)
                  </p>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-8 text-gray-500">
            No holdings yet. Start investing to see your portfolio here!
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
