import React, { useState, useEffect } from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip } from 'recharts';
import { TrendingUp, TrendingDown } from 'lucide-react';
import api from '../utils/api';

const Portfolio = () => {
  const [portfolioData, setPortfolioData] = useState(null);
  const [loading, setLoading] = useState(true);
  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchPortfolioData();
  }, []);

  const fetchPortfolioData = async () => {
    try {
      const response = await api.getPortfolio(token);
      const data = await response.json();
      setPortfolioData(data);
    } catch (error) {
      console.error('Error fetching portfolio:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="flex justify-center items-center h-64">Loading...</div>;
  }

  if (!portfolioData) {
    return <div className="text-center text-red-600">Error loading portfolio data</div>;
  }

  const { holdings, totalInvested, currentValue, totalGain, gainPercentage } = portfolioData;

  // Calculate allocation data for pie chart
  const allocationData = holdings.reduce((acc, holding) => {
    const value = holding.currentValue;
    const existingType = acc.find(item => item.name === holding.asset_type);
    if (existingType) {
      existingType.value += value;
    } else {
      acc.push({ name: holding.asset_type, value });
    }
    return acc;
  }, []);

  const COLORS = ['#3b82f6', '#22c55e', '#f59e0b', '#ef4444'];

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Portfolio</h1>
        <p className="text-gray-600">Track your investments and performance</p>
      </div>

      {/* Portfolio Summary */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2">
          <div className="card">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-lg font-semibold text-gray-900">Holdings</h2>
              <div className="text-right">
                <p className="text-sm text-gray-600">Total Value</p>
                <p className="text-xl font-bold text-gray-900">₹{currentValue.toLocaleString()}</p>
                <p className={`text-sm ${totalGain > 0 ? 'text-green-600' : 'text-red-600'}`}>
                  {totalGain > 0 ? '+' : ''}₹{totalGain.toLocaleString()} ({gainPercentage}%)
                </p>
              </div>
            </div>

            {holdings.length > 0 ? (
              <div className="overflow-x-auto">
                <table className="min-w-full">
                  <thead>
                    <tr className="border-b border-gray-200">
                      <th className="text-left py-3 px-4 font-medium text-gray-900">Asset</th>
                      <th className="text-right py-3 px-4 font-medium text-gray-900">Qty</th>
                      <th className="text-right py-3 px-4 font-medium text-gray-900">Avg Price</th>
                      <th className="text-right py-3 px-4 font-medium text-gray-900">Current Price</th>
                      <th className="text-right py-3 px-4 font-medium text-gray-900">P&L</th>
                    </tr>
                  </thead>
                  <tbody>
                    {holdings.map((holding) => (
                      <tr key={holding.id} className="border-b border-gray-100 hover:bg-gray-50">
                        <td className="py-4 px-4">
                          <div>
                            <p className="font-medium text-gray-900">{holding.asset_symbol}</p>
                            <p className="text-sm text-gray-600">{holding.asset_name}</p>
                            <span className="inline-block px-2 py-1 text-xs bg-gray-100 text-gray-600 rounded-full mt-1">
                              {holding.asset_type}
                            </span>
                          </div>
                        </td>
                        <td className="text-right py-4 px-4 text-gray-900">{holding.quantity}</td>
                        <td className="text-right py-4 px-4 text-gray-900">₹{holding.avg_buy_price}</td>
                        <td className="text-right py-4 px-4 text-gray-900">₹{holding.currentPrice}</td>
                        <td className="text-right py-4 px-4">
                          <div className={`flex items-center justify-end ${holding.pnl > 0 ? 'text-green-600' : 'text-red-600'}`}>
                            {holding.pnl > 0 ? <TrendingUp className="h-4 w-4 mr-1" /> : <TrendingDown className="h-4 w-4 mr-1" />}
                            <div>
                              <p className="font-medium">{holding.pnl > 0 ? '+' : ''}₹{holding.pnl.toFixed(2)}</p>
                              <p className="text-sm">({holding.pnlPercentage}%)</p>
                            </div>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <div className="text-center py-8 text-gray-500">
                No holdings yet. Start investing to build your portfolio!
              </div>
            )}
          </div>
        </div>

        {/* Asset Allocation Pie Chart */}
        <div className="card">
          <h2 className="text-lg font-semibold text-gray-900 mb-6">Asset Allocation</h2>
          {allocationData.length > 0 ? (
            <>
              <div className="h-64">
                <ResponsiveContainer width="100%" height="100%">
                  <PieChart>
                    <Pie
                      data={allocationData}
                      cx="50%"
                      cy="50%"
                      innerRadius={60}
                      outerRadius={100}
                      paddingAngle={5}
                      dataKey="value"
                    >
                      {allocationData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                      ))}
                    </Pie>
                    <Tooltip formatter={(value) => `₹${value.toLocaleString()}`} />
                  </PieChart>
                </ResponsiveContainer>
              </div>
              <div className="mt-4 space-y-2">
                {allocationData.map((entry, index) => (
                  <div key={entry.name} className="flex items-center justify-between">
                    <div className="flex items-center">
                      <div 
                        className="w-3 h-3 rounded-full mr-2"
                        style={{ backgroundColor: COLORS[index % COLORS.length] }}
                      />
                      <span className="text-sm text-gray-600">{entry.name}</span>
                    </div>
                    <span className="text-sm font-medium text-gray-900">
                      {((entry.value / currentValue) * 100).toFixed(1)}%
                    </span>
                  </div>
                ))}
              </div>
            </>
          ) : (
            <div className="text-center py-8 text-gray-500">
              No assets to display
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Portfolio;
