import React, { useState, useEffect } from 'react';
import { Plus, TrendingUp, TrendingDown, Heart, Search, X } from 'lucide-react';
import api from '../utils/api';
import BuyModal from '../components/BuyModal';
import SellModal from '../components/SellModal';
import SearchBar from '../components/SearchBar';
import useAutoRefresh from '../hooks/useAutoRefresh';

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [newStock, setNewStock] = useState({ symbol: '', name: '' });
  const [selectedStock, setSelectedStock] = useState(null);
  const [showBuyModal, setShowBuyModal] = useState(false);
  const [showSellModal, setShowSellModal] = useState(false);
  const [userHoldings, setUserHoldings] = useState([]);
  const token = localStorage.getItem('token');

  const fetchWatchlist = async () => {
    try {
      const response = await api.getWatchlist(token);
      const data = await response.json();
      setWatchlist(data);
    } catch (error) {
      console.error('Error fetching watchlist:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchUserHoldings = async () => {
    try {
      const response = await api.getPortfolio(token);
      const data = await response.json();
      setUserHoldings(data.holdings || []);
    } catch (error) {
      console.error('Error fetching holdings:', error);
    }
  };

  useEffect(() => {
    fetchWatchlist();
    fetchUserHoldings();
  }, []);

  // Auto-refresh every 30 seconds
  useAutoRefresh(() => {
    fetchWatchlist();
    fetchUserHoldings();
  }, 30000);

  const removeFromWatchlist = async (symbol) => {
    try {
      const response = await api.removeFromWatchlist(token, symbol);
      if (response.ok) {
        fetchWatchlist();
      }
    } catch (error) {
      console.error('Error removing from watchlist:', error);
    }
  };

  const addStockToWatchlist = async (stock) => {
    try {
      const response = await api.addToWatchlist(token, {
        symbol: stock.symbol,
        assetName: stock.name
      });

      if (response.ok) {
        fetchWatchlist();
      }
    } catch (error) {
      console.error('Error adding to watchlist:', error);
    }
  };

  const filteredWatchlist = watchlist.filter(stock =>
    stock.asset_symbol.toLowerCase().includes(searchTerm.toLowerCase()) ||
    stock.asset_name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const WatchlistCard = ({ stock }) => (
    <div className="card hover:shadow-md transition-shadow cursor-pointer">
      <div className="flex items-center justify-between">
        <div className="flex-1">
          <div className="flex items-center justify-between mb-2">
            <h3 className="font-semibold text-gray-900">{stock.asset_symbol}</h3>
            <button 
              onClick={(e) => {
                e.stopPropagation();
                removeFromWatchlist(stock.asset_symbol);
              }}
              className="p-1 hover:bg-red-50 rounded-full transition-colors"
              title="Remove from watchlist"
            >
              <X className="h-4 w-4 text-red-500" />
            </button>
          </div>
          <p className="text-sm text-gray-600 mb-3">{stock.asset_name}</p>
          <div className="flex items-center justify-between">
            <div>
              <p className="text-xl font-bold text-gray-900">₹{Number(stock.currentPrice || 0).toFixed(2)}</p>
            </div>
            <div className={`flex items-center ${stock.change > 0 ? 'text-green-600' : 'text-red-600'}`}>
              {stock.change > 0 ? <TrendingUp className="h-4 w-4 mr-1" /> : <TrendingDown className="h-4 w-4 mr-1" />}
              <div className="text-right">
                <p className="text-sm font-medium">
                  {(stock.change || 0) > 0 ? '+' : ''}₹{Math.abs(stock.change || 0).toFixed(2)}
                </p>
                <p className="text-xs">
                  ({(stock.changePercentage || 0) > 0 ? '+' : ''}{Number(stock.changePercentage || 0).toFixed(2)}%)
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="mt-4 flex space-x-2">
        <button 
          onClick={(e) => {
            e.stopPropagation();
            setSelectedStock({
              symbol: stock.asset_symbol,
              name: stock.asset_name || stock.asset_symbol,
              currentPrice: stock.currentPrice,
              type: stock.asset_type || 'Stock'
            });
            setShowBuyModal(true);
          }}
          className="flex-1 btn-success text-sm py-1"
        >
          Buy
        </button>
        <button 
          onClick={(e) => {
            e.stopPropagation();
            const holding = userHoldings.find(h => h.asset_symbol === stock.asset_symbol);
            if (holding) {
              setSelectedStock({
                symbol: stock.asset_symbol,
                name: stock.asset_name || stock.asset_symbol,
                currentPrice: stock.currentPrice,
                type: stock.asset_type || 'Stock'
              });
              setShowSellModal(true);
            } else {
              alert('You don\'t own this stock');
            }
          }}
          className="flex-1 btn-danger text-sm py-1"
          disabled={!userHoldings.find(h => h.asset_symbol === stock.asset_symbol)}
        >
          Sell
        </button>
      </div>
    </div>
  );

  if (loading) {
    return <div className="flex justify-center items-center h-64">Loading...</div>;
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Watchlist</h1>
          <p className="text-gray-600">Keep track of your favorite stocks and investments</p>
        </div>
        <button 
          onClick={() => setShowAddModal(true)}
          className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
        >
          <Plus className="h-4 w-4 mr-2" />
          Add Stock
        </button>
      </div>

      {/* Search Bar */}
      <div className="card">
        <div className="relative">
          <div className="absolute inset-y-0 left-0 flex items-center pl-3">
            <Search className="h-5 w-5 text-gray-400" />
          </div>
          <input
            type="text"
            placeholder="Search stocks in your watchlist..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="block w-full rounded-md border border-gray-300 bg-white py-2 pl-10 pr-3 text-sm placeholder-gray-500 focus:border-blue-500 focus:outline-none focus:ring-1 focus:ring-blue-500"
          />
        </div>
      </div>

      {/* Watchlist Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredWatchlist.map((stock) => (
          <WatchlistCard key={stock.id} stock={stock} />
        ))}
      </div>

      {filteredWatchlist.length === 0 && (
        <div className="text-center py-12">
          <Heart className="h-12 w-12 text-gray-300 mx-auto mb-4" />
          <p className="text-gray-500 text-lg">No stocks in your watchlist</p>
          <p className="text-gray-400">Add some stocks to get started!</p>
        </div>
      )}

      {/* Add Stock Modal with Search */}
      {showAddModal && (
        <div className="fixed inset-0 z-50 overflow-y-auto">
          <div className="flex items-center justify-center min-h-screen px-4">
            <div className="fixed inset-0 bg-gray-600 bg-opacity-75" onClick={() => setShowAddModal(false)} />
            <div className="relative bg-white rounded-lg max-w-lg w-full p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Add to Watchlist</h3>
              <SearchBar 
                onStockSelect={(stock) => {
                  addStockToWatchlist(stock);
                  setShowAddModal(false);
                }}
                onBuyStock={(stock) => {
                  addStockToWatchlist(stock);
                  setShowAddModal(false);
                }}
                placeholder="Search stocks, ETFs, mutual funds to add..."
              />
              <div className="mt-4 flex justify-end">
                <button 
                  onClick={() => setShowAddModal(false)}
                  className="px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Buy Modal */}
      {showBuyModal && selectedStock && (
        <BuyModal 
          stock={selectedStock}
          onClose={() => {
            setShowBuyModal(false);
            setSelectedStock(null);
          }}
          onSuccess={() => {
            fetchWatchlist();
            fetchUserHoldings();
          }}
        />
      )}

      {/* Sell Modal */}
      {showSellModal && selectedStock && (
        <SellModal 
          stock={selectedStock}
          holding={userHoldings.find(h => h.asset_symbol === selectedStock.asset_symbol)}
          onClose={() => {
            setShowSellModal(false);
            setSelectedStock(null);
          }}
          onSuccess={() => {
            fetchWatchlist();
            fetchUserHoldings();
          }}
        />
      )}
    </div>
  );
};

export default Watchlist;
