import React, { useState, useEffect } from 'react';
import { Plus, Target, Calendar, TrendingUp } from 'lucide-react';
import api from '../utils/api';

const Goals = () => {
  const [goals, setGoals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddModal, setShowAddModal] = useState(false);
  const [newGoal, setNewGoal] = useState({
    goalName: '',
    targetAmount: '',
    targetDate: '',
    monthlyInvestment: ''
  });
  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchGoals();
  }, []);

  const fetchGoals = async () => {
    try {
      const response = await api.getGoals(token);
      const data = await response.json();
      setGoals(data);
    } catch (error) {
      console.error('Error fetching goals:', error);
    } finally {
      setLoading(false);
    }
  };

  const addGoal = async () => {
    try {
      const response = await api.addGoal(token, {
        goalName: newGoal.goalName,
        targetAmount: parseFloat(newGoal.targetAmount),
        targetDate: newGoal.targetDate,
        monthlyInvestment: parseFloat(newGoal.monthlyInvestment) || 0
      });

      if (response.ok) {
        fetchGoals();
        setShowAddModal(false);
        setNewGoal({ goalName: '', targetAmount: '', targetDate: '', monthlyInvestment: '' });
      }
    } catch (error) {
      console.error('Error adding goal:', error);
    }
  };

  const GoalCard = ({ goal }) => {
    const monthsRemaining = Math.ceil(
      (new Date(goal.target_date) - new Date()) / (1000 * 60 * 60 * 24 * 30)
    );

    return (
      <div className="card">
        <div className="flex items-start justify-between mb-4">
          <div className="flex items-center">
            <div className="p-2 bg-blue-50 rounded-lg mr-3">
              <Target className="h-6 w-6 text-blue-600" />
            </div>
            <div>
              <h3 className="font-semibold text-gray-900">{goal.goal_name}</h3>
              <p className="text-sm text-gray-600">Target: ₹{parseFloat(goal.target_amount).toLocaleString()}</p>
            </div>
          </div>
          <span className="text-xs bg-blue-50 text-blue-600 px-2 py-1 rounded-full">
            {monthsRemaining > 0 ? `${monthsRemaining} months left` : 'Overdue'}
          </span>
        </div>

        <div className="mb-4">
          <div className="flex justify-between text-sm mb-2">
            <span className="text-gray-600">Progress</span>
            <span className="font-medium text-gray-900">{goal.progress}%</span>
          </div>
          <div className="w-full bg-gray-200 rounded-full h-2">
            <div 
              className="bg-blue-600 h-2 rounded-full transition-all duration-300"
              style={{ width: `${Math.min(goal.progress, 100)}%` }}
            />
          </div>
        </div>

        <div className="grid grid-cols-2 gap-4 mb-4">
          <div>
            <p className="text-xs text-gray-600">Current Amount</p>
            <p className="font-semibold text-gray-900">₹{parseFloat(goal.current_amount).toLocaleString()}</p>
          </div>
          <div>
            <p className="text-xs text-gray-600">Monthly SIP</p>
            <p className="font-semibold text-gray-900">₹{parseFloat(goal.monthly_investment).toLocaleString()}</p>
          </div>
        </div>

        <div className="flex items-center justify-between pt-4 border-t border-gray-100">
          <div className="flex items-center text-sm text-gray-600">
            <Calendar className="h-4 w-4 mr-1" />
            Target: {new Date(goal.target_date).toLocaleDateString('en-IN', {
              month: 'short',
              year: 'numeric'
            })}
          </div>
          <button className="text-blue-600 hover:text-blue-700 text-sm font-medium">
            View Details
          </button>
        </div>
      </div>
    );
  };

  if (loading) {
    return <div className="flex justify-center items-center h-64">Loading...</div>;
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Financial Goals</h1>
          <p className="text-gray-600">Set and track your investment goals</p>
        </div>
        <button 
          onClick={() => setShowAddModal(true)}
          className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
        >
          <Plus className="h-4 w-4 mr-2" />
          Add Goal
        </button>
      </div>

      {/* Goals Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {goals.map((goal) => (
          <GoalCard key={goal.id} goal={goal} />
        ))}
      </div>

      {goals.length === 0 && (
        <div className="text-center py-12">
          <Target className="h-12 w-12 text-gray-300 mx-auto mb-4" />
          <p className="text-gray-500 text-lg">No goals set yet</p>
          <p className="text-gray-400">Create your first financial goal!</p>
        </div>
      )}

      {/* Investment Suggestions */}
      <div className="card">
        <div className="flex items-center mb-4">
          <TrendingUp className="h-5 w-5 text-blue-600 mr-2" />
          <h2 className="text-lg font-semibold text-gray-900">Investment Suggestions</h2>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="p-4 bg-blue-50 rounded-lg">
            <h3 className="font-medium text-blue-900 mb-2">Conservative</h3>
            <p className="text-sm text-blue-700 mb-3">Low risk, steady returns</p>
            <ul className="text-xs text-blue-600 space-y-1">
              <li>• Fixed Deposits</li>
              <li>• Government Bonds</li>
              <li>• Debt Mutual Funds</li>
            </ul>
          </div>
          <div className="p-4 bg-green-50 rounded-lg">
            <h3 className="font-medium text-green-900 mb-2">Moderate</h3>
            <p className="text-sm text-green-700 mb-3">Balanced risk and returns</p>
            <ul className="text-xs text-green-600 space-y-1">
              <li>• Hybrid Mutual Funds</li>
              <li>• Blue-chip Stocks</li>
              <li>• Index Funds</li>
            </ul>
          </div>
          <div className="p-4 bg-orange-50 rounded-lg">
            <h3 className="font-medium text-orange-900 mb-2">Aggressive</h3>
            <p className="text-sm text-orange-700 mb-3">High risk, high returns</p>
            <ul className="text-xs text-orange-600 space-y-1">
              <li>• Growth Stocks</li>
              <li>• Equity Mutual Funds</li>
              <li>• Sector ETFs</li>
            </ul>
          </div>
        </div>
      </div>

      {/* Add Goal Modal */}
      {showAddModal && (
        <div className="fixed inset-0 z-50 overflow-y-auto">
          <div className="flex items-center justify-center min-h-screen px-4">
            <div className="fixed inset-0 bg-gray-600 bg-opacity-75" onClick={() => setShowAddModal(false)} />
            <div className="relative bg-white rounded-lg max-w-md w-full p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Create New Goal</h3>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Goal Title
                  </label>
                  <input
                    type="text"
                    placeholder="e.g., Buy a Car, House Down Payment"
                    value={newGoal.goalName}
                    onChange={(e) => setNewGoal({...newGoal, goalName: e.target.value})}
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Target Amount (₹)
                  </label>
                  <input
                    type="number"
                    placeholder="500000"
                    value={newGoal.targetAmount}
                    onChange={(e) => setNewGoal({...newGoal, targetAmount: e.target.value})}
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Target Date
                  </label>
                  <input
                    type="date"
                    value={newGoal.targetDate}
                    onChange={(e) => setNewGoal({...newGoal, targetDate: e.target.value})}
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Monthly Investment (₹)
                  </label>
                  <input
                    type="number"
                    placeholder="10000"
                    value={newGoal.monthlyInvestment}
                    onChange={(e) => setNewGoal({...newGoal, monthlyInvestment: e.target.value})}
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>
                <div className="flex space-x-3">
                  <button 
                    onClick={() => setShowAddModal(false)}
                    className="flex-1 px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50"
                  >
                    Cancel
                  </button>
                  <button 
                    onClick={addGoal}
                    className="flex-1 btn-primary"
                  >
                    Create Goal
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Goals;
